var product = Product.find('first');

Rho.RhoConnectClient.addObjectNotify('Product', product.id);

var page = params.page || params.pageSize,
    pageSize = 10;
Rho.RhoConnectClient.search(
  {
    'Contact',
    'from': 'search',
    'search_params': {
      'FirstName': params.FirstName,
      'LastName': params.LastName,
      'Company': params.Company
    },
    'offset': page,
    'max_results': pageSize
  },
  searchCallback
);

CODE.RUBY:
def search_callback
  status = @params["status"]
  if (status and status == "ok")
  WebView.navigate(
    url_for(
      :action => :show_page,
      :query => @params['search_params']
    )
  )
  else
    render :action => :search_error
  end
end

def show_page
  @contacts = Contact.find(
    :all,
    :conditions => {
    {
      :func => 'LOWER',
      :name => 'FirstName',
      :op => 'LIKE'
    } => @params[:FirstName],
    {
      :func => 'LOWER',
      :name=>'LastName',
      :op=>'LIKE'
    } => @params[:LastName],
    {
      :func=>'LOWER',
      :name=>'Company',
      :op=>'LIKE'
    } => @params[:Company],
    },
    :op => 'OR',
    :select => ['FirstName','LastName', 'Company'],
    :per_page => page_size,
    :offset => page * page_size
  )
  render :action => :show_page
end

